import React, { Component, createContext } from 'react';

type FormProps = {
    action: string;
    method: string;
    onSubmit(): void;
}

interface IFormState {
    values: Map<string, string>;
    error: boolean;
    submitted: boolean;
}

interface IFormContext extends IFormState {

}

class Form extends Component<FormProps, {}> {
    getMethod() {
        const { method } = this.props;
        if (method.toLowerCase() == 'post' || method.toLowerCase() == 'get') {
            return method.toUpperCase();
        }
        return 'POST';
    }

    render() {
        return (
            <form action={this.props.action} method={this.getMethod()} onSubmit={this.props.onSubmit}>
                {this.props.children}
            </form>
        )
    }
}

export default Form;
