import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './image.css';

type SingleImageProp = {
    // Image information
    imageId: number;

    // Gallery information
    galleryName: string;
}
// TODO: Get the right path for the image from files server -> imageSource
// TODO: Get the right path for the image from files server -> imageSource

class SingleImage extends Component<SingleImageProp, {}> {
    render() {
        const imageLink = `viewer/${this.props.galleryName}/${this.props.imageId.toString()}`;
        const imageSource = `https://via.placeholder.com/450`; // TODO: Get the right path for the image from files server.

        return (
            <div className="single-image">
                <Link to={imageLink}>
                    <img src={imageSource} alt={this.props.imageId.toString()} />
                </Link>
            </div>
        )
    }
}

export default SingleImage;
