import styled from 'styled-components';
import { GalleryButton } from '../../Button';

export const ListContainer = styled('div')`
    display: flex;
    flex-wrap: nowrap;
    justify-content: center;
    width: 80%;
    margin: auto;

    @media (max-width: 850px) {
        flex-direction: column;
    }
`;
