        if (!this.state.canLoad) {
            return;
        }
        const imageService = new ImageService();
        try {
            const { nbImagesLoaded, images } = this.state;
            const newImages: Image[] = await imageService.getKImagesFromOffset(NB_IMAGE_PER_FETCH, nbImagesLoaded);

