import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './image.css';

type RecentImageProp = {
    // Image information
    imageId: number;
    imageType: string;
    date: Date;

    // Gallery information
    galleryDisplayName: string;
    galleryName: string;
}

// TODO: Change imageType to a enum ImageType, overkill ?
// TODO: Figure out if image is landscape or portrait
// TODO: Get the right path for the image from files server -> imageSource

class RecentImage extends Component<RecentImageProp, {}> {
    render() {
        const formatedDate = this.props.date.toLocaleDateString('en-us', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
        });
        const galleryLink = `gallery/${this.props.galleryName}`;
        const imageLink = `viewer/${this.props.galleryName}/${this.props.imageId.toString()}`;
        const imageSource = `https://via.placeholder.com/125x450`;
        
        return (
            <div className="recent-image-container">
                <div className="info">
                    <Link className="category" to={galleryLink}>{this.props.galleryDisplayName}</Link>
                    <h3 className="date">{formatedDate}</h3>
                </div>
                <div className="image">
                    <Link to={imageLink}>
                        <img className={this.props.imageType} src={imageSource} alt={this.props.imageId.toString()}/>
                    </Link>
                </div>
            </div>
        )
    }
}

export default RecentImage;
