import React, { Component, createContext, FormEvent } from 'react';
import { stringify } from 'querystring';

type FormProps = {
    action: string;
    method: string;
    onSubmit(): void;
}

type FormState = {
    values: Map<string, string>;
    error: boolean;
    submitted: boolean;
}

export type FormContextType = FormState & {
    setValue(key: string, value: string): void;
}

export const FormContext = createContext<FormContextType>({
    setValue: (key: string, value: string) => {
        throw new Error('Not yet implemented');
    },
    values: new Map<string, string>(),
    error: false,
    submitted: false
});

class Form extends Component<FormProps, FormState> {
    constructor(props: FormProps) {
        super(props);
        this.state = {
            values: new Map<string, string>(),
            submitted: false,
            error: false
        }
    }

    getMethod() {
        const { method } = this.props;
        if (method.toLowerCase() == 'post' || method.toLowerCase() == 'get') {
            return method.toUpperCase();
        }
        return 'POST';
    }

    setValue(key: string, value: string) {
        console.log(this.state);
    }

    handleSubmit(event: FormEvent<HTMLFormElement>) {
        event.preventDefault();
        console.log(this.state.values)
    }

    render() {
        const context: FormContextType = {
            ...this.state,
            setValue: this.setValue
        }
        return (
            <FormContext.Provider value={context}>
                <form onSubmit={(e: FormEvent<HTMLFormElement>) => this.handleSubmit(e)} action={this.props.action} method={this.getMethod()}>
                    <input type="text" />
                    <button type="submit">Submit</button>
                </form>
            </FormContext.Provider>
        )
    }
}

export default Form;
