import styled, { css } from 'styled-components';

const RecentImageContainer = styled('div')`
    display: inline-block;
    position: relative;
    max-width: 850px;
    margin-left: auto;
    margin-right: auto;
    border-radius: 15px;
    box-shadow: 0px 0px 25px rgba(0, 0, 0, .15);
    margin: 10px;
    transform: none;
    transition: transform .35s;
    :hover {
        transform: scale(1.015);
    }
`;

const ImgInfoHeader = styled('div')`
    padding-top: 4px;
    height: 50px;  
`;

const ImgInfo = styled('div')`
    float: left;
    margin-left: 25px;
    font-family: "CooperHewitt Medium";
    font-size: .8em;
    color: #7E7E7E;
`;

// Change to a regular button -> add type small
const ImgCategory = styled('button')`
    float: left;
    margin-left: 25px;

    margin-block-start: 1em;
    margin-block-end: 1em;
    font-weight: bold;
    text-decoration: none;
    transition: color 0.4s;
    :hover {
        color: black; 
    }
`;

const ImgDate = styled('div')`
    float: right;
    margin-right: 25px;
`

export {
    RecentImageContainer,
    ImgInfoHeader,
    ImgInfo,
    ImgDate,
}
