import React, { Component, FormEvent } from 'react';
import { StyledInput } from './input-style';
import { Margin, MarginTop, Block, MarginLeft } from '../../regular/style';
import { IFormContext, FormContext } from '../form/Form';

type InputProps = {
    id: string;
    label?: string;
    placeholder?: string;
    errorMessage?: string;
    hideContent?: boolean;
    required?: boolean;
}

class Input extends Component<InputProps, {}> {
    getType() {
        return this.props.hideContent ? 'password' : 'text';
    }

    handleChange(event: FormEvent<HTMLInputElement>, context?: IFormContext) {
        const { id } = this.props;
        const value = event.currentTarget.value;
        context?.setValue(id, value);
    }

    render() {
        const { id, label, placeholder } = this.props;
        const error = false;
        return (
            <FormContext.Consumer>
                {(context: IFormContext) => (
                    <Block>
                        <Margin amount={10}>
                            {label && (
                                <MarginLeft amount={5}>
                                    <label htmlFor={id}>{label}</label>
                                </MarginLeft>
                            )}
                            <MarginTop amount={5}>
                                <StyledInput id={id} error={error} placeholder={placeholder} type={this.getType()} onChange={(e: FormEvent<HTMLInputElement>) => this.handleChange(e, context)}/>
                            </MarginTop>
                        </Margin>
                    </Block>
                )}
            </FormContext.Consumer>
        )
    }
} 

export default Input;
