import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './round-button.css';

type ArrowButtonProp = {
    direction: string;
    onClick: () => void;
}

type ArrowButtonState = {
    direction: string;
}

class ArrowButton extends Component<ArrowButtonProp, ArrowButtonState> {
    private readonly availableDirection: string[] = ['up', 'right', 'left', 'down'];

    constructor(props: ArrowButtonProp) {
        super(props);
        this.state = {
            direction: ''
        }
    }

    setDirection(direction: string) {
        if (this.availableDirection.includes(direction)) {
            this.setState({ direction });
        } else {
            this.setState({ direction: 'left' });
        }
    }

    componentDidMount() {
        this.setDirection(this.props.direction);
    }

    render() {
        const arrowClass: string = `fas fa-arrow-${this.state.direction}`;

        return (
            <button onClick={() => this.props.onClick()} className="round-button">
                <div className="icon-container">
                    <div className="icon">
                        <i className="fas fa-times" />
                    </div> 
                </div>
            </button>
        )
    }
}

export default ArrowButton;
