import React, { Component, Fragment } from 'react';
import { StyledInput } from './input-style';
import { Margin, MarginTop, Block, MarginLeft } from '../../regular/style';
import { FormContext } from '../form/Form';

type InputProps = {
    id: string;
    label?: string;
    value?: string;
    placeholder?: string;
    errorMessage?: string;
    hideContent?: boolean;
    required?: boolean;
}

type InputState = {
    error: boolean;
}

class Input extends Component<InputProps, InputState> {
    constructor(props: InputProps) {
        super(props);
        this.state = {
            error: false
        }
    }
    getType() {
        return this.props.hideContent ? 'password' : 'text';
    }

    render() {
        const { id, label, placeholder, value } = this.props;
        const { error } = this.state;
        return (
            <Block>
                <Margin amount={10}>
                    {label && (
                        <MarginLeft amount={5}>
                            <label htmlFor={id}>{label}</label>
                        </MarginLeft>
                    )}
                    <MarginTop amount={5}>
                        <StyledInput id={id} error={error} placeholder={placeholder} type={this.getType()} value={value}/>
                    </MarginTop>
                </Margin>
            </Block>
        )
    }
} 

export default Input;
