import styled, { css } from 'styled-components';

const RegularButton = styled('button')<{ variant: string, size: string }>`
  display: inline-block;

  font-size: ${props => props.size === 'small' ? .8 : 1.2}em;
  font-weight: ${props => props.variant === 'light' ? 'bold' : 'normal'};
  
  border: none;
  font-family: "CooperHewitt Medium";
  text-decoration: none;
  transform: none;

  ${props => props.variant === 'light' ? css`
    padding: 5px;
  ` : css`
    padding: 15px 35px;
  `}

  height: ${props => props.variant === 'light' ? 'auto' : '50px'};
  width: auto;
  background-color: white;

  :focus {
    outline: 0;
  }

  :hover {
    cursor: pointer;
    ${props => props.variant === 'light' ? css`
      color: black;
    ` : css`
      transform: scale(1.05);
    `}
  }

  ${props => props.variant === 'light' ? 
  css`
    color: #7E7E7E;
    transition: color .25s;
  ` : 
  css`
    color: black;
    border-radius: 8px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.15);
    transition: transform .5s;
  `}
`;

export default RegularButton;
