import React, { Component, FormEvent } from 'react';
import { StyledInput } from './input-style';
import { Margin, MarginTop, Block, MarginLeft } from '../../regular/style';
import { FormContext, FormContextType } from '../form/Form';

type InputProps = {
    id: string;
    label?: string;
    value?: string;
    placeholder?: string;
    errorMessage?: string;
    hideContent?: boolean;
    required?: boolean;
}

class Input extends Component<InputProps, {}> {
    constructor(props: InputProps) {
        super(props);
        this.state = {
            error: false
        }
    }
    getType() {
        return this.props.hideContent ? 'password' : 'text';
    }

    handleChange(event: FormEvent<HTMLInputElement>, context: FormContextType) {
        context.setValue(this.props.id, event.currentTarget.value);
    }

    render() {
        const { id, label, placeholder, value } = this.props;
        const { error } = this.state;
        return (
            <FormContext.Consumer>
                {(context: FormContextType) => (
                    <Block>
                        <Margin amount={10}>
                            {label && (
                                <MarginLeft amount={5}>
                                    <label htmlFor={id}>{label}</label>
                                </MarginLeft>
                            )}
                            <MarginTop amount={5}>
                                <StyledInput id={id} error={error} placeholder={placeholder} type={this.getType()} value={value} onChange={(e: FormEvent<HTMLInputElement>) => this.handleChange(e, context)}/>
                            </MarginTop>
                        </Margin>
                    </Block>
                )}
            </FormContext.Consumer>
        )
    }
} 

export default Input;
