import React, { Component } from 'react';
import { StyledInput } from './input-style';

type InputProps = {
    id: string;
    name: string;
    value?: string;
    placeholder?: string;
    errorMessage?: string;
    hideContent?: boolean;
    required?: boolean;
}

type InputState = {
    error: boolean;
}

class Input extends Component<InputProps, InputState> {
    constructor(props: InputProps) {
        super(props);
    }
    getType() {
        return this.props.hideContent ? 'password' : 'text';
    }

    render() {
        const { id, name, placeholder, value } = this.props;
        const { error } = this.state;
        return (
            <StyledInput id={id} name={name} error={error} placeholder={placeholder} type={this.getType()} value={value}/>
        )
    }
} 

export default Input;
