import React, { Component, FormEvent } from 'react';
import { StyledInput } from './input-style';
import { Margin, MarginTop, Block, MarginLeft } from '../../regular/style';

type InputProps = {
    id: string;
    onChange(event: FormEvent<HTMLInputElement>): void;
    label?: string;
    value?: string;
    placeholder?: string;
    errorMessage?: string;
    hideContent?: boolean;
    required?: boolean;
}

class Input extends Component<InputProps, {}> {
    getType() {
        return this.props.hideContent ? 'password' : 'text';
    }

    handleChange(event: FormEvent<HTMLInputElement>) {
        this.props.onChange(event);
    }

    render() {
        const { id, label, placeholder, value } = this.props;
        const error = false;
        return (
            <Block>
                <Margin amount={10}>
                    {label && (
                        <MarginLeft amount={5}>
                            <label htmlFor={id}>{label}</label>
                        </MarginLeft>
                    )}
                    <MarginTop amount={5}>
                        <StyledInput id={id} error={error} placeholder={placeholder} type={this.getType()} value={value} onChange={(e: FormEvent<HTMLInputElement>) => this.handleChange(e)}/>
                    </MarginTop>
                </Margin>
            </Block>
        )
    }
} 

export default Input;
