import React, { Component, FormEvent, Fragment } from 'react';
import styled from 'styled-components';
import Parallax from '../../UI/Parallax/Parallax';

import Landing from '../../Landing/Landing';
import GalleriesPreview from '../../GalleriesPreview/GalleriesPreview';
import About from '../../About/About';

import Image from '../../../helper/Image';
import Gallery from '../../../helper/Gallery';
import HttpRequest from '../../../helper/HttpRequest';

interface HomeState {
    galleries: Gallery[];
    error: boolean;
    loading: boolean
}

class Home extends Component<{}, HomeState> {
    state = {
        galleries: [],
        error: false,
        errorMessage: '',
        loading: true,
    }

    setError(errorMessage: string) {
        this.setState({ error: true, loading: false });
    }

    handleFetchError(data: any | null): boolean {
        if (!data) {
            this.setError('Something unexptected happened. Please try again later.');
            return true;
        }
        if (data.error) {
            this.setError(data.error.message);
            return true;
        }
        return false;
    }

    async fetchGalleries() {
        try {
            const data: any | null = await HttpRequest.getData('http://localhost:8000/galleries/limit/3');

            if (!this.handleFetchError(data)) {
                const galleries: Gallery[] = data.map((gallery: Gallery) => {
                    const thumbnail: Image = new Image(gallery.thumbnail.id, gallery.id, new Date(gallery.thumbnail.uploadDate));
                    const formattedGallery: Gallery = new Gallery(gallery.id, gallery.displayName, thumbnail);
                    return formattedGallery;
                });
                this.setState({ loading: false, galleries });
            }
        } catch (e) {
            this.setError('Something unexptected happened. Please try again later');
        }
    }

    componentDidMount() {
        this.fetchGalleries();
    }

    render() {
        return (
            <Fragment>
                <Parallax img="images/parallax-1.jpg" speed={0.5}>
                    <Landing />
                </Parallax>
                <GalleriesPreview galleries={this.state.galleries} />
                <Parallax img="images/parallax-2.jpg" speed={0.5} >
                    <About />
                </Parallax>
            </Fragment>
        )
    }
}

export default Home;
