import React, { Component } from 'react';
import { Text } from '../regular/text';
import { Absolute, AbsoluteWithWidth } from '../regular/positionning';
import { Info } from './info-display';

interface IInfoDisplayProps {
    info: string;
    position: string;
    maxWidth?: number;
    minWidth?: number;
}

interface IPosition {
    top?: number;
    bottom?: number;
    left?: number;
    right?: number;
}

const POSITIONS: string[] = ['bottom-right', 'top-right', 'bottom-left', 'top-left'];

/* TODO:
    - Position -> top-left, top-right, ...
    - Info -> what is being displayed
    - max/minWidth -> if it has a size (Absolute / AbsoluteWidthSize)
    - 
*/

class InfoDisplay extends Component<IInfoDisplayProps, {}> {
    getPosition() {
        let { position } = this.props;
        if (POSITIONS.includes(position)) {
            return position;
        }
        return POSITIONS[0];
    }
    render() {
        const positions = this.getPosition().split('-');
        const top = positions[0] === 'top' ? -25 : undefined;
        const bottom = positions[0] === 'bottom' ? -25 : undefined;
        const left = positions[1] === 'left' ? -25 : undefined;
        const right = positions[1] === 'right' ? -25 : undefined;

        return (
            <Absolute top={top} bottom={bottom} left={left} right={right}>
                <Info>
                    <Text size="medium" color="black" weight="normal">{this.props.info}</Text>
                </Info>
            </Absolute>
        )
    }
}

export default InfoDisplay;
